import { useState } from "react";

// const initialItems = [
//   { id: 1, description: "Passports", quantity: 2, packed: true },
//   { id: 2, description: "Socks", quantity: 12, packed: false },
//   { id: 3, description: "Charger", quantity: 2, packed: false },
//   { id: 4, description: "Hat", quantity: 3, packed: true }
// ];


export default function App() {

  /**lift up because Form & Packinglist components need it */
  const [items, setItems] = useState([]);

  //derived state
  const numItems = items.length;


  /**destruct current array and add new item to brand new list */
  function handleAddItems(item) {
    setItems(items => [...items, item]);
  }

  function handleDeleteItems(id) {
    setItems(items => items.filter(item => item.id !== id));
  }


  function handleToggleItem(id) {
    setItems(items =>
      items.map(item => item.id === id ?
        { ...item, packed: !item.packed }
        : item)
    )
  }


  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList
        items={items}
        onDeleteItem={handleDeleteItems}
        onUpdateItem={handleToggleItem} />
      <Stats />
    </div>
  );
}


function Logo() {
  return (
    <h1>üçÑ Far Away üèùÔ∏è</h1>
  )
}

function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);


  function handleSubmit(e) {
    /**this is to prevent default ENTER submit */
    e.preventDefault();

    /**if no despcriton then add wont work */
    if (!description) return;


    const newItem = { description, quantity, packed: false, id: Date.now() };
    console.log(newItem);

    onAddItems(newItem);

    /**after form submitted, set back to original state */
    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your trip üëÄ</h3>
      <select
        value={quantity}
        onChange={e => setQuantity(Number(e.target.value))}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map
          ((num) => (
            <option value={num} key={num}>
              {num}
            </option>
          ))}
      </select>
      <input type="text" placeholder="Items" value={description}
        onChange={e => {
          //console.log(e.target);
          setDescription(e.target.value)
        }} />
      <button>Add</button>
    </form>
  )
}

function Item({ itemObject, onDeleteItem, onUpdateItem }) {
  return (
    <li>
      <input type="checkbox"
        value={itemObject.packed}
        onChange={() => onUpdateItem(itemObject.id)} />
      <span style={itemObject.packed ? { textDecoration: 'line-through' } : {}}>
        {itemObject.description} {itemObject.quantity}</span>
      {/* simply put onDeleteItem wont work needs to pass the id */}
      < button onClick={() => onDeleteItem(itemObject.id)}>‚ùå</button>
    </li >
  )
}

function PackingList({ items, onDeleteItem, onUpdateItem }) {
  return (
    <div className="list">
      <ul>
        {items.map(item =>
          <Item itemObject={item} key={item.id}
            onDeleteItem={onDeleteItem}
            onUpdateItem={onUpdateItem}
          />)}
      </ul>

    </div>
  )

}

function Stats() {
  return (
    <footer className="stats">
      <em>üß≥ You have x items on your list, and you already packed </em>
    </footer >
  )

}
