import { useState } from "react";
import Logo from './Logo';
import Form from './Form';
import { PackingList } from "./PackingList";

export default function App() {

  /**lift up because Form & Packinglist components need it */
  const [items, setItems] = useState([]);


  /**destruct current array and add new item to brand new list */
  function handleAddItems(item) {
    setItems(items => [...items, item]);
  }

  function handleDeleteItems(id) {
    setItems(items => items.filter(item => item.id !== id));
  }


  function handleToggleItem(id) {
    setItems(items =>
      items.map(item => item.id === id ?
        { ...item, packed: !item.packed }
        : item)
    )
  }

  function handleClearItems() {
    const confirmed
      = window.confirm("Are you sure you want to delete all items");

    if (confirmed) setItems([]);
  }


  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList
        items={items}
        onDeleteItem={handleDeleteItems}
        onUpdateItem={handleToggleItem}
        onClearItem={handleClearItems} />
      <Stats items={items} />
    </div>
  );
}




export function Item({ itemObject, onDeleteItem, onUpdateItem }) {
  return (
    <li>
      <input type="checkbox"
        value={itemObject.packed}
        onChange={() => onUpdateItem(itemObject.id)} />
      <span style={itemObject.packed ? { textDecoration: 'line-through' } : {}}>
        {itemObject.description} {itemObject.quantity}</span>
      {/* simply put onDeleteItem wont work needs to pass the id */}
      < button onClick={() => onDeleteItem(itemObject.id)}>‚ùå</button>
    </li >
  )
}

function Stats({ items }) {
  //early return of condtion rendering 
  if (!items.length)
    return (
      <p className="stats">
        <em>Start addding items to you packing list üß≥</em>
      </p>
    )

  //derived state
  const numItems = items.length;
  const numPacked = items.filter(item => item.packed).length;
  const percentage = Math.round(numPacked / numItems * 100);

  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? `‚úàÔ∏è You are ready to go `
          : `üß≥ You have ${numItems} items on your list, and you already packed ${numPacked}
           (${percentage}%)`}
      </em>
    </footer >
  )

}
