import { useState } from "react";
import Logo from './Logo';


export default function App() {

  /**lift up because Form & Packinglist components need it */
  const [items, setItems] = useState([]);


  /**destruct current array and add new item to brand new list */
  function handleAddItems(item) {
    setItems(items => [...items, item]);
  }

  function handleDeleteItems(id) {
    setItems(items => items.filter(item => item.id !== id));
  }


  function handleToggleItem(id) {
    setItems(items =>
      items.map(item => item.id === id ?
        { ...item, packed: !item.packed }
        : item)
    )
  }

  function handleClearItems() {
    const confirmed
      = window.confirm("Are you sure you want to delete all items");

    if (confirmed) setItems([]);
  }


  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList
        items={items}
        onDeleteItem={handleDeleteItems}
        onUpdateItem={handleToggleItem}
        onClearItem={handleClearItems} />
      <Stats items={items} />
    </div>
  );
}


function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);


  function handleSubmit(e) {
    /**this is to prevent default ENTER submit */
    e.preventDefault();

    /**if no despcriton then add wont work */
    if (!description) return;


    const newItem = { description, quantity, packed: false, id: Date.now() };
    console.log(newItem);

    onAddItems(newItem);

    /**after form submitted, set back to original state */
    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your trip üëÄ</h3>
      <select
        value={quantity}
        onChange={e => setQuantity(Number(e.target.value))}>
        {Array.from({ length: 20 }, (_, i) => i + 1).map
          ((num) => (
            <option value={num} key={num}>
              {num}
            </option>
          ))}
      </select>
      <input type="text" placeholder="Items" value={description}
        onChange={e => {
          //console.log(e.target);
          setDescription(e.target.value)
        }} />
      <button>Add</button>
    </form>
  )
}

function Item({ itemObject, onDeleteItem, onUpdateItem }) {
  return (
    <li>
      <input type="checkbox"
        value={itemObject.packed}
        onChange={() => onUpdateItem(itemObject.id)} />
      <span style={itemObject.packed ? { textDecoration: 'line-through' } : {}}>
        {itemObject.description} {itemObject.quantity}</span>
      {/* simply put onDeleteItem wont work needs to pass the id */}
      < button onClick={() => onDeleteItem(itemObject.id)}>‚ùå</button>
    </li >
  )
}

function PackingList({ items, onDeleteItem, onUpdateItem, onClearItem }) {

  const [sortBy, setSortBy] = useState('packed');
  //derived state for sorting 
  let sortedItems;
  if (sortBy === "input") sortedItems = items;
  if (sortBy === "description")
    sortedItems = items.slice().sort((a, b) => a.description.localeCompare(b.description));
  if (sortBy === "packed")
    sortedItems = items.slice().sort((a, b) => Number(a.packed) - Number(b.packed));


  return (
    <div className="list">
      <ul>
        {sortedItems.map(item =>
          <Item itemObject={item} key={item.id}
            onDeleteItem={onDeleteItem}
            onUpdateItem={onUpdateItem}
          />)}
      </ul>

      <div className="actions">
        <select value={sortBy}
          onChange={e => setSortBy(e.target.value)}>
          <option value='input'>sort by input order</option>
          <option value='description'>sort by description</option>
          <option value='packed'>sort by packed status</option>
        </select>
        <button onClick={onClearItem}>Clear List</button>
      </div>
    </div >
  )

}

function Stats({ items }) {
  //early return of condtion rendering 
  if (!items.length)
    return (
      <p className="stats">
        <em>Start addding items to you packing list üß≥</em>
      </p>
    )

  //derived state
  const numItems = items.length;
  const numPacked = items.filter(item => item.packed).length;
  const percentage = Math.round(numPacked / numItems * 100);

  return (
    <footer className="stats">
      <em>
        {percentage === 100
          ? `‚úàÔ∏è You are ready to go `
          : `üß≥ You have ${numItems} items on your list, and you already packed ${numPacked}
           (${percentage}%)`}
      </em>
    </footer >
  )

}
